--
-- Table structure for table `wp_posts`
--

DROP TABLE IF EXISTS `wp_posts`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `wp_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(20) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=InnoDB AUTO_INCREMENT=332 DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

--
-- Dumping data for table `wp_posts`
--

LOCK TABLES `wp_posts` WRITE;
/*!40000 ALTER TABLE `wp_posts` DISABLE KEYS */;
INSERT INTO `wp_posts` VALUES (1,1,'2014-01-01 18:49:06','2014-01-01 18:49:06','Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!','Hello world!','','publish','open','open','','hello-world','','','2014-01-01 18:49:06','2014-01-01 18:49:06','',0,'http://players.azurewebsites.net/?p=1',0,'post','',1),(11,1,'2014-01-01 18:49:06','2014-01-01 18:49:06','<a href=\"http://players.azurewebsites.net/wp-content/uploads/2014/01/Panto-poster-2014-2.jpg\"><img class=\"alignnone size-full wp-image-301\" alt=\"Panto poster 2014 (2)\" src=\"http://players.azurewebsites.net/wp-content/uploads/2014/01/Panto-poster-2014-2.jpg\" width=\"167\" height=\"244\" /></a>\r\n\r\nWhy not come along to our next show \'Once Upon A Time\'?\r\n\r\nCall us on 01242 522 567 for more information and to book your seats.','Home','','publish','closed','open','','sample-page','','','2014-01-06 22:56:13','2014-01-06 22:56:13','',0,'http://players.azurewebsites.net/?page_id=2',0,'page','',0),(21,1,'2014-01-01 18:49:25','0000-00-00 00:00:00','','Auto Draft','','auto-draft','open','open','','','','','2014-01-01 18:49:25','0000-00-00 00:00:00','',0,'http://players.azurewebsites.net/?p=21',0,'post','',0),(31,1,'2014-01-01 18:51:03','2014-01-01 18:51:03','This is the home page.','Home','','inherit','open','open','','11-revision-v1','','','2014-01-01 18:51:03','2014-01-01 18:51:03','',11,'http://players.azurewebsites.net/?p=31',0,'revision','',0),(41,1,'2014-01-01 18:58:49','2014-01-01 18:58:49','If you would like any more information about any aspect of our society, please email <a href=\"mailto:info@leckhamptonplayers.com\">info@leckhamptonplayers.com</a> where your enquiry will be dealt with by the relevant person.\r\n\r\nYou can also join us on social media sites, Twitter and Facebook:\r\n\r\n@LeckyPlayers\r\n\r\nLeckhampton Players\r\n<p align=\"center\"><b><span style=\"text-decoration: underline;\">Our Home</span></b><b><span style=\"text-decoration: underline;\"> </span></b></p>\r\n<p align=\"center\">Each of our rehearsals and Productions take place at:</p>\r\n<p align=\"center\">Leckhampton Village Hall</p>\r\n<p style=\"text-align: center;\" align=\"center\">Church Road</p>\r\n<p align=\"center\">Leckhampton</p>\r\n<p align=\"center\">Cheltenham</p>\r\n<p align=\"center\">Glos. GL53 0PL</p>','Contact Us','','publish','closed','closed','','contact-us','','','2014-01-05 22:07:30','2014-01-05 22:07:30','',0,'http://players.azurewebsites.net/?page_id=41',0,'page','',0),(51,1,'2014-01-01 18:58:49','2014-01-01 18:58:49','Contact us page.','Contact Us','','inherit','closed','closed','','41-revision-v1','','','2014-01-01 18:58:49','2014-01-01 18:58:49','',41,'http://players.azurewebsites.net/?p=51',0,'revision','',0),(61,11,'2014-01-01 21:57:30','0000-00-00 00:00:00','','Auto Draft','','auto-draft','closed','closed','','','','','2014-01-01 21:57:30','0000-00-00 00:00:00','',0,'http://players.azurewebsites.net/?p=61',0,'post','',0),(81,11,'2014-01-05 21:57:20','0000-00-00 00:00:00','','Auto Draft','','auto-draft','closed','closed','','','','','2014-01-05 21:57:20','0000-00-00 00:00:00','',0,'http://players.azurewebsites.net/?page_id=81',0,'page','',0),(101,11,'2014-01-05 22:05:53','2014-01-05 22:05:53','If you would like any more information about any aspect of our society, please email <a href=\"mailto:info@leckhamptonplayers.com\">info@leckhamptonplayers.com</a> where your enquiry will be dealt with by the relevant person.\n\nYou can also join us on social media sites, Twitter and Facebook:\n\n@LeckyPlayers\n\nLeckhampton Players\n<p align=\"center\"><b><span style=\"text-decoration: underline;\">Our Home</span></b></p>\n<p align=\"center\"><b><span style=\"text-decoration: underline;\"> </span></b></p>\n<p align=\"center\">Each of our rehearsals and Productions take place at:</p>\n&nbsp;\n<p align=\"center\">Leckhampton Village Hall</p>\n<p align=\"center\">Church Road</p>\n<p align=\"center\">Leckhampton</p>\n<p align=\"center\">Cheltenham</p>\n<p align=\"center\">Glos. GL53 0PL</p>','Contact Us','','inherit','closed','closed','','41-autosave-v1','','','2014-01-05 22:05:53','2014-01-05 22:05:53','',41,'http://players.azurewebsites.net/?p=101',0,'revision','',0),(111,11,'2014-01-05 22:07:30','2014-01-05 22:07:30','If you would like any more information about any aspect of our society, please email <a href=\"mailto:info@leckhamptonplayers.com\">info@leckhamptonplayers.com</a> where your enquiry will be dealt with by the relevant person.\r\n\r\nYou can also join us on social media sites, Twitter and Facebook:\r\n\r\n@LeckyPlayers\r\n\r\nLeckhampton Players\r\n<p align=\"center\"><b><span style=\"text-decoration: underline;\">Our Home</span></b><b><span style=\"text-decoration: underline;\"> </span></b></p>\r\n<p align=\"center\">Each of our rehearsals and Productions take place at:</p>\r\n<p align=\"center\">Leckhampton Village Hall</p>\r\n<p style=\"text-align: center;\" align=\"center\">Church Road</p>\r\n<p align=\"center\">Leckhampton</p>\r\n<p align=\"center\">Cheltenham</p>\r\n<p align=\"center\">Glos. GL53 0PL</p>','Contact Us','','inherit','closed','closed','','41-revision-v1','','','2014-01-05 22:07:30','2014-01-05 22:07:30','',41,'http://players.azurewebsites.net/?p=111',0,'revision','',0),(121,11,'2014-01-05 22:09:58','2014-01-05 22:09:58','In May 2012, Kate Coleman expressed an interest in staging a youth production with the support of the Leckhampton Players. This was discussed by the Players’ committee and subsequently agreed that this was something the society would like to pursue.\r\n\r\nDan Peacey had previously talked about this but had been unable to find the time before now, he was then installed as the link between the main society and the youth players.\r\n\r\nAlong with Zoe Blower and Chris Brooker, this team of four have been instrumental in bringing together children of all abilities to form a branch of our society that is the future of the Leckhampton Players.\r\n\r\nIn the early stages they decided to arrange summer workshops for children between the ages of 10 and 18. Through July and August last year they ran these workshops. The potential of this youth society quickly became evident so it was agreed that they would continue with monthly workshops.\r\n\r\nFollowing the success of these workshops, it was clear that the youth players would benefit from having something to aim towards, and were given the opportunity of staging a one act play as part of the Players’ spring production.\r\n\r\nAfter this production, monthly workshops continued, and the society were delighted that 12 of our youth players were able to take part in our 2014 pantomime ‘Once upon a time’.\r\n\r\nNot only this, but two of our older youth player members were cast in and took significant roles in the Players Autumn 2013 production, ‘Old Time Music Hall’.\r\n\r\nUltimately, we intend to bring these youth members together and using the knowledge, experience and skills of Zoe, Chris, Kate and Dan continue to build a youth society that will, in years to come, secure the future of this very special amateur dramatics society.\r\n\r\nThe Youth Players continue to meet once per month from 7pm – 8.30pm at Leckhampton Village Hall.\r\n\r\nIf you are or have children, grandchildren, nieces or nephews between the ages of 10 and 18 and would like to be a part of the Leckhampton Youth Players please email <a href=\"mailto:info@leckhamptonplayers.com\">info@leckhamptonplayers.com</a>','Youth Players','','publish','closed','closed','','youth-players','','','2014-01-05 22:10:04','2014-01-05 22:10:04','',0,'http://players.azurewebsites.net/?page_id=121',0,'page','',0),(131,11,'2014-01-05 22:09:58','2014-01-05 22:09:58','In May 2012, Kate Coleman expressed an interest in staging a youth production with the support of the Leckhampton Players. This was discussed by the Players’ committee and subsequently agreed that this was something the society would like to pursue.\r\n\r\nDan Peacey had previously talked about this but had been unable to find the time before now, he was then installed as the link between the main society and the youth players.\r\n\r\nAlong with Zoe Blower and Chris Brooker, this team of four have been instrumental in bringing together children of all abilities to form a branch of our society that is the future of the Leckhampton Players.\r\n\r\nIn the early stages they decided to arrange summer workshops for children between the ages of 10 and 18. Through July and August last year they ran these workshops. The potential of this youth society quickly became evident so it was agreed that they would continue with monthly workshops.\r\n\r\nFollowing the success of these workshops, it was clear that the youth players would benefit from having something to aim towards, and were given the opportunity of staging a one act play as part of the Players’ spring production.\r\n\r\nAfter this production, monthly workshops continued, and the society were delighted that 12 of our youth players were able to take part in our 2014 pantomime ‘Once upon a time’.\r\n\r\nNot only this, but two of our older youth player members were cast in and took significant roles in the Players Autumn 2013 production, ‘Old Time Music Hall’.\r\n\r\nUltimately, we intend to bring these youth members together and using the knowledge, experience and skills of Zoe, Chris, Kate and Dan continue to build a youth society that will, in years to come, secure the future of this very special amateur dramatics society.\r\n\r\nThe Youth Players continue to meet once per month from 7pm – 8.30pm at Leckhampton Village Hall.\r\n\r\nIf you are or have children, grandchildren, nieces or nephews between the ages of 10 and 18 and would like to be a part of the Leckhampton Youth Players please email <a href=\"mailto:info@leckhamptonplayers.com\">info@leckhamptonplayers.com</a>','Youth Players','','inherit','closed','closed','','121-revision-v1','','','2014-01-05 22:09:58','2014-01-05 22:09:58','',121,'http://players.azurewebsites.net/?p=131',0,'revision','',0),(141,11,'2014-01-05 22:19:34','0000-00-00 00:00:00','','Auto Draft','','auto-draft','closed','closed','','','','','2014-01-05 22:19:34','0000-00-00 00:00:00','',0,'http://players.azurewebsites.net/?page_id=141',0,'page','',0),(151,11,'2014-01-05 22:22:04','2014-01-05 22:22:04','<p align=\"center\"><b><span style=\"text-decoration: underline;\">ONCE UPON A TIME</span></b></p>\r\nWritten and directed by Chris Brooker, this year’s pantomime promises to be a real night of family fun, loaded with local references, panto gags and of course, audience participation.\r\n\r\nJoin Dame Lotta and her family of fairy tale and nursery rhyme children as they seek to find out who is stealing their stories! Along the way, they meet Yankee Doodle Dandy and his friends, as the circus rolls into Town. It doesn’t take Dan long to fall in love with our Principal Girl, Polly Flinders. Meanwhile WWW Dot Com leads the way in trying to ruin nursery rhyme land. Will She Succeed?\r\n\r\nOnce Upton a Time runs from 25<sup>th</sup> January – 1<sup>st</sup> February 2014.\r\n\r\nSat 25<sup>th</sup> Jan: 2pm &amp; 7.30pm\r\n\r\nSun 26<sup>th</sup> Jan: 3pm\r\n\r\nMon 27<sup>th</sup> Jan: NO PERFORMANCE\r\n\r\nTues 28<sup>th</sup> Jan: 7.30pm\r\n\r\nWeds 29<sup>th</sup> Jan: 7.30pm\r\n\r\nThurs 30<sup>th</sup> Jan: 7.30pm\r\n\r\nFri 31<sup>st</sup> Jan: 7.30pm\r\n\r\nSat 1<sup>st</sup> Feb: 2pm &amp; 7.30pm\r\n\r\n&nbsp;\r\n<p align=\"center\">Book your tickets now!</p>\r\n<p align=\"center\"><b>£7.00 Adult £4.00 Child</b></p>\r\n<p align=\"center\"><b>01242 522567 between 10am and 8pm daily.</b></p>','Next Production','','publish','closed','closed','','next-production','','','2014-01-05 22:22:08','2014-01-05 22:22:08','',0,'http://players.azurewebsites.net/?page_id=151',0,'page','',0),(161,11,'2014-01-05 22:22:04','2014-01-05 22:22:04','<p align=\"center\"><b><span style=\"text-decoration: underline;\">ONCE UPON A TIME</span></b></p>\r\nWritten and directed by Chris Brooker, this year’s pantomime promises to be a real night of family fun, loaded with local references, panto gags and of course, audience participation.\r\n\r\nJoin Dame Lotta and her family of fairy tale and nursery rhyme children as they seek to find out who is stealing their stories! Along the way, they meet Yankee Doodle Dandy and his friends, as the circus rolls into Town. It doesn’t take Dan long to fall in love with our Principal Girl, Polly Flinders. Meanwhile WWW Dot Com leads the way in trying to ruin nursery rhyme land. Will She Succeed?\r\n\r\nOnce Upton a Time runs from 25<sup>th</sup> January – 1<sup>st</sup> February 2014.\r\n\r\nSat 25<sup>th</sup> Jan: 2pm &amp; 7.30pm\r\n\r\nSun 26<sup>th</sup> Jan: 3pm\r\n\r\nMon 27<sup>th</sup> Jan: NO PERFORMANCE\r\n\r\nTues 28<sup>th</sup> Jan: 7.30pm\r\n\r\nWeds 29<sup>th</sup> Jan: 7.30pm\r\n\r\nThurs 30<sup>th</sup> Jan: 7.30pm\r\n\r\nFri 31<sup>st</sup> Jan: 7.30pm\r\n\r\nSat 1<sup>st</sup> Feb: 2pm &amp; 7.30pm\r\n\r\n&nbsp;\r\n<p align=\"center\">Book your tickets now!</p>\r\n<p align=\"center\"><b>£7.00 Adult £4.00 Child</b></p>\r\n<p align=\"center\"><b>01242 522567 between 10am and 8pm daily.</b></p>','Next Production','','inherit','closed','closed','','151-revision-v1','','','2014-01-05 22:22:04','2014-01-05 22:22:04','',151,'http://players.azurewebsites.net/?p=161',0,'revision','',0),(171,11,'2014-01-05 22:24:40','2014-01-05 22:24:40','The Leckhampton Players were founded in 1947 and have enjoyed over 65 years running as a successful amateur dramatics society, based in Leckhampton, Cheltenham.\r\n\r\nAs a society, we produce 3 shows each year. Typically, the format for this is a pantomime in late January / early February, a musical production in spring time, normally late May and a play in the autumn, usually mid-October.\r\n\r\nRehearsals take place on Tuesday, Thursday and Sunday Evenings, however the director of each individual production will release a rehearsal schedule specifically for their show.\r\n\r\nWe are always keen to welcome new members, both on stage on off. So, if your talent doesn’t include acting or singing there are many more aspects of our society that require people to partake to help us continue to run so successfully. For example, Scenery making and painting, backstage scene shifters, Wardrobe assistance, lighting and sound, front of house and bar staff.\r\n\r\nIf you would like more information on how you could join the players please email <a href=\"mailto:info@leckhamptonplayers.com\">info@leckhamptonplayers.com</a>\r\n\r\nPlease also ‘Join Us’ online:\r\n\r\n@LeckyPlayers\r\n\r\nLeckhampton Players','Join Us','','publish','closed','closed','','join-us','','','2014-01-05 22:24:44','2014-01-05 22:24:44','',0,'http://players.azurewebsites.net/?page_id=171',0,'page','',0),(181,11,'2014-01-05 22:24:40','2014-01-05 22:24:40','The Leckhampton Players were founded in 1947 and have enjoyed over 65 years running as a successful amateur dramatics society, based in Leckhampton, Cheltenham.\r\n\r\nAs a society, we produce 3 shows each year. Typically, the format for this is a pantomime in late January / early February, a musical production in spring time, normally late May and a play in the autumn, usually mid-October.\r\n\r\nRehearsals take place on Tuesday, Thursday and Sunday Evenings, however the director of each individual production will release a rehearsal schedule specifically for their show.\r\n\r\nWe are always keen to welcome new members, both on stage on off. So, if your talent doesn’t include acting or singing there are many more aspects of our society that require people to partake to help us continue to run so successfully. For example, Scenery making and painting, backstage scene shifters, Wardrobe assistance, lighting and sound, front of house and bar staff.\r\n\r\nIf you would like more information on how you could join the players please email <a href=\"mailto:info@leckhamptonplayers.com\">info@leckhamptonplayers.com</a>\r\n\r\nPlease also ‘Join Us’ online:\r\n\r\n@LeckyPlayers\r\n\r\nLeckhampton Players','Join Us','','inherit','closed','closed','','171-revision-v1','','','2014-01-05 22:24:40','2014-01-05 22:24:40','',171,'http://players.azurewebsites.net/?p=181',0,'revision','',0),(191,21,'2014-01-06 22:05:10','2014-01-06 22:05:10','Test','Test','','trash','closed','closed','','test','','','2014-01-06 22:23:22','2014-01-06 22:23:22','',0,'http://players.azurewebsites.net/?p=191',0,'post','',0),(201,21,'2014-01-06 22:05:10','2014-01-06 22:05:10','Test','Test','','inherit','closed','closed','','191-revision-v1','','','2014-01-06 22:05:10','2014-01-06 22:05:10','',191,'http://players.azurewebsites.net/?p=201',0,'revision','',0),(211,21,'2014-01-06 22:05:10','0000-00-00 00:00:00','','Auto Draft','','auto-draft','closed','closed','','','','','2014-01-06 22:05:10','0000-00-00 00:00:00','',0,'http://players.azurewebsites.net/?p=211',0,'post','',0),(221,21,'2014-01-06 22:16:30','2014-01-06 22:16:30','Welcome to the new Leckhampton Players website. Over the next few weeks we will be adding content to the site although you can already read a little about our history and the find ways to contact us.\r\n\r\nYou can also find information about our next show, a Pantomime called \'Once Upon a Time\', which opens on Saturday 25th January and runs until Saturday 1st February. There are still a few tickets available and you\'d be very welcome to come along. Please call 01242 522567 for more information and to book your seats.\r\n\r\nAnd don\'t forget to bookmark our site to keep up to date with the latest news from Leckhampton Players!','Welcome to our new website','','publish','closed','closed','','welcome-to-the-new-website','','','2014-01-06 22:16:42','2014-01-06 22:16:42','',0,'http://players.azurewebsites.net/?p=221',0,'post','',0),(231,21,'2014-01-06 22:16:30','2014-01-06 22:16:30','Welcome to the new Leckhampton Players website. Over the next f